module.exports = (service) => ({
  getAll: async () => {
    const prizes = await service.db.collection('prizes')
      .where('description', '>', '')
      .get();

    const response = [];

    prizes.forEach((d) => {
      response.push(Object.assign({}, d.data(), {
        id: d.id,
      }));
    });

    return response;
  },
});